import React, { useState } from 'react';

export default function Contact() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');

    const handleInputChange = (e) => {

    const { name, value } = e.target;

    // Ternary statement that will call either setFirstName or setLastName based on what field the user is typing in
    return name === 'firstName' ? setFirstName(value) : setLastName(value);
    if (name === "name"){
      setName(value);
    } else if  (name === "email"){
      setEmail(value);
    } else if (name === message){
      setMessage(value);
    }

    };

    const handleFormSubmit = (e) => {
    e.preventDefault();

    alert(`Contact has been sent from ${name} ${email}`);
    setName('');
    setEmail('');
    setMessage('');
  };

  return (
      <div className="main container p-3">
        <section>
          <h1>Contact</h1>
          <form>
            <div className="form-group">
              <label>Name</label>
              <input type="text" autoComplete="off" placeholder="Enter your name" className="form-control" required />
               <div class="invalid-feedback">
                  Error: Name is required information.
              </div>
            </div>
            <div className="form-group">
              <label>Email address</label> 
              <input type="email" autoComplete="off" placeholder="Enter your email address" className="form-control" required />
              <div class="invalid-feedback">
                  Error: Email is required information.
              </div>
               <div class="invalid-feedback">
                  Error: Email format is incorrect.
              </div>
            </div>
            <div className="form-group">
              <label>Message</label>
              <textarea className="form-control" placeholder="Enter your message"></textarea>
               <div class="invalid-feedback">
                  Error: Message is required information.
              </div>
            </div>
            <div>
              <button className="btn" type="submit">Submit</button>
            </div>
          </form> 
        </section>
      </div>
   );
}
